    fn _create_generator(group: &BigNumber,
                         predicate: &Fn(&BigNumber, &BigNumber) -> bool) -> BigNumber {
        let mut g;
        loop {
            g = BigNumber::rand_range(group).unwrap();
            if predicate(&g, group) {
                break;
            }
        }

        g
    }

