/// Credentials owner that can proof and partially disclose the credentials to verifier.
pub struct Prover {}

impl Prover {
    /// Creates a master secret.
    ///
    /// # Example
    /// ```
    /// use indy_crypto::cl::prover::Prover;
    ///
    /// let _master_secret = Prover::new_master_secret().unwrap();
    /// ```
    pub fn new_master_secret() -> Result<MasterSecret, IndyCryptoError> {
        Ok(MasterSecret { ms: bn_rand(LARGE_MASTER_SECRET)? })
    }

    /// Creates blinded master secret for given issuer key and master secret.
    ///
    /// # Arguments
    /// * `credential_pub_key` - Credential public keys.
    /// * `credential_key_correctness_proof` - Credential key correctness proof.
    /// * `master_secret` - Master secret.
    /// * `master_secret_blinding_nonce` - Nonce used for creation of blinded_master_secret_correctness_proof.
    ///
    /// # Example
    /// ```
    /// use indy_crypto::cl::new_nonce;
    /// use indy_crypto::cl::issuer::Issuer;
    /// use indy_crypto::cl::prover::Prover;
    ///
    /// let mut credential_schema_builder = Issuer::new_credential_schema_builder().unwrap();
    /// credential_schema_builder.add_attr("sex").unwrap();
    /// let credential_schema = credential_schema_builder.finalize().unwrap();
    ///
    /// let (credential_pub_key, _credential_priv_key, cred_key_correctness_proof) = Issuer::new_credential_def(&credential_schema, false).unwrap();
    ///
    /// let master_secret = Prover::new_master_secret().unwrap();
    /// let master_secret_blinding_nonce = new_nonce().unwrap();
    /// let (_blinded_master_secret, _master_secret_blinding_data, _blinded_master_secret_correctness_proof) =
    ///     Prover::blind_credential_secrets(&credential_pub_key,
    ///                                 &cred_key_correctness_proof,
    ///                                 &master_secret,
    ///                                 &master_secret_blinding_nonce).unwrap();
    /// ```
    pub fn blind_credential_secrets(
        credential_pub_key: &CredentialPublicKey,
        credential_key_correctness_proof: Option<&CredentialKeyCorrectnessProof>,
        credential_values: &CredentialValues,
        credential_nonce: Option<&Nonce>,
    ) -> Result<
        (BlindedCredentialSecrets,
         CredentialSecretsBlindingFactors,
         Option<BlindedCredentialSecretsCorrectnessProof>),
        IndyCryptoError,
    > {
        trace!(
            "Prover::blind_credential_secrets: >>> credential_pub_key: {:?}, \
                                                      credential_key_correctness_proof: {:?}, \
                                                      credential_values: {:?}, \
                                                      credential_nonce: {:?}",
            credential_pub_key,
            credential_key_correctness_proof,
            credential_values,
            credential_nonce
        );

        if credential_key_correctness_proof.is_some() {
            Prover::_check_credential_key_correctness_proof(
                &credential_pub_key.p_key,
                credential_key_correctness_proof.unwrap(),
            )?;
        }

        let primary_blinded_credential_secrets =
            Prover::_generate_primary_blinded_credential_secrets(
                &credential_pub_key.p_key,
                &credential_values,
            )?;

        let blinded_revocation_master_secret = match credential_pub_key.r_key {
            Some(ref r_pk) => Some(Prover::_generate_revocation_blinded_credential_secrets(
                r_pk,
            )?),
            _ => None,
        };

        let blinded_credential_secrets_correctness_proof = match credential_nonce {
            Some(nonce) => Some(Prover::_new_blinded_credential_secrets_correctness_proof(
                &credential_pub_key.p_key,
                &primary_blinded_credential_secrets,
                &nonce,
                &credential_values,
            )?),
            None => None,
        };

        let blinded_credential_secrets = BlindedCredentialSecrets {
            u: primary_blinded_credential_secrets.u,
            ur: blinded_revocation_master_secret.as_ref().map(|d| d.ur),
            hidden_attributes: primary_blinded_credential_secrets.hidden_attributes,
            committed_attributes: primary_blinded_credential_secrets.committed_attributes,
        };

        let credential_secrets_blinding_factors = CredentialSecretsBlindingFactors {
            v_prime: primary_blinded_credential_secrets.v_prime,
            vr_prime: blinded_revocation_master_secret.map(|d| d.vr_prime),
        };

        trace!(
            "Prover::blind_credential_secrets: <<< blinded_credential_secrets: {:?}, \
                                                      credential_secrets_blinding_factors: {:?}, \
                                                      blinded_credential_secrets_correctness_proof: {:?},",
            blinded_credential_secrets,
            credential_secrets_blinding_factors,
            blinded_credential_secrets_correctness_proof
        );

        Ok((
            blinded_credential_secrets,
            credential_secrets_blinding_factors,
            blinded_credential_secrets_correctness_proof,
        ))
    }

    /// Updates the credential signature by a master secret blinding data.
    ///
    /// # Arguments
    /// * `credential_signature` - Credential signature generated by Issuer.
    /// * `credential_values` - Credential values.
    /// * `signature_correctness_proof` - Credential signature correctness proof.
    /// * `master_secret_blinding_data` - Master secret blinding data.
    /// * `master_secret` - Master secret.
    /// * `credential_pub_key` - Credential public key.
    /// * `nonce` -  Nonce was used by Issuer for the creation of signature_correctness_proof.
    /// * `rev_key_pub` - (Optional) Revocation registry public key.
    /// * `rev_reg` - (Optional) Revocation registry.
    /// * `witness` - (Optional) Witness.
    ///
    /// # Example
    /// ```
    /// use indy_crypto::cl::new_nonce;
    /// use indy_crypto::cl::issuer::Issuer;
    /// use indy_crypto::cl::prover::Prover;
    ///
    /// let mut credential_schema_builder = Issuer::new_credential_schema_builder().unwrap();
    /// credential_schema_builder.add_attr("sex").unwrap();
    /// let credential_schema = credential_schema_builder.finalize().unwrap();
    ///
    /// let (credential_pub_key, credential_priv_key, cred_key_correctness_proof) = Issuer::new_credential_def(&credential_schema, false).unwrap();
    ///
    /// let master_secret = Prover::new_master_secret().unwrap();
    /// let master_secret_blinding_nonce = new_nonce().unwrap();
    /// let (blinded_master_secret, master_secret_blinding_data, blinded_master_secret_correctness_proof) =
    ///     Prover::blind_master_secret(&credential_pub_key, &cred_key_correctness_proof, &master_secret, &master_secret_blinding_nonce).unwrap();
    ///
    /// let mut credential_values_builder = Issuer::new_credential_values_builder().unwrap();
    /// credential_values_builder.add_value("sex", "5944657099558967239210949258394887428692050081607692519917050011144233115103").unwrap();
    /// let credential_values = credential_values_builder.finalize().unwrap();
    ///
    /// let credential_issuance_nonce = new_nonce().unwrap();
    ///
    /// let (mut credential_signature, signature_correctness_proof) =
    ///     Issuer::sign_credential("CnEDk9HrMnmiHXEV1WFgbVCRteYnPqsJwrTdcZaNhFVW",
    ///                             &blinded_master_secret,
    ///                             &blinded_master_secret_correctness_proof,
    ///                             &master_secret_blinding_nonce,
    ///                             &credential_issuance_nonce,
    ///                             &credential_values,
    ///                             &credential_pub_key,
    ///                             &credential_priv_key).unwrap();
    ///
    /// Prover::process_credential_signature(&mut credential_signature,
    ///                                      &credential_values,
    ///                                      &signature_correctness_proof,
    ///                                      &master_secret_blinding_data,
    ///                                      &master_secret,
    ///                                      &credential_pub_key,
    ///                                      &credential_issuance_nonce,
    ///                                      None, None, None).unwrap();
    /// ```
    pub fn process_credential_signature(
        credential_signature: &mut CredentialSignature,
        credential_values: &CredentialValues,
        signature_correctness_proof: Option<&SignatureCorrectnessProof>,
        credential_secrets_blinding_factors: &CredentialSecretsBlindingFactors,
        credential_pub_key: &CredentialPublicKey,
        nonce: Option<&Nonce>,
        rev_key_pub: Option<&RevocationKeyPublic>,
        rev_reg: Option<&RevocationRegistry>,
        witness: Option<&Witness>,
    ) -> Result<(), IndyCryptoError> {
        trace!(
            "Prover::process_credential_signature: >>> credential_signature: {:?}, \
                                                          credential_values: {:?}, \
                                                          signature_correctness_proof: {:?}, \
                                                          credential_secrets_blinding_factors: {:?}, \
                                                          credential_pub_key: {:?}, \
                                                          nonce: {:?}, \
                                                          rev_key_pub: {:?}, \
                                                          rev_reg: {:?}, \
                                                          witness: {:?}",
            credential_signature,
            credential_values,
            signature_correctness_proof,
            credential_secrets_blinding_factors,
            credential_pub_key,
            nonce,
            rev_key_pub,
            rev_reg,
            witness
        );

        Prover::_process_primary_credential(
            &mut credential_signature.p_credential,
            &credential_secrets_blinding_factors.v_prime,
        )?;

        match (signature_correctness_proof, nonce) {
            (Some(prf), Some(cred_nonce)) => {
                Prover::_check_signature_correctness_proof(
                    &credential_signature.p_credential,
                    credential_values,
                    prf,
                    &credential_pub_key.p_key,
                    cred_nonce,
                )?
            }
            _ => (),
        }

        if let (&mut Some(ref mut non_revocation_cred),
                Some(ref vr_prime),
                &Some(ref r_key),
                Some(ref r_key_pub),
                Some(ref r_reg),
                Some(ref witness)) =
            (
                &mut credential_signature.r_credential,
                credential_secrets_blinding_factors.vr_prime,
                &credential_pub_key.r_key,
                rev_key_pub,
                rev_reg,
                witness,
            )
        {
            Prover::_process_non_revocation_credential(
                non_revocation_cred,
                vr_prime,
                &r_key,
                r_key_pub,
                r_reg,
                witness,
            )?;
        }

        trace!("Prover::process_credential_signature: <<<");

        Ok(())
    }

    /// Creates and returns proof builder.
    ///
    /// The purpose of proof builder is building of proof entity according to the given request .
    /// # Example
    /// ```
    /// use indy_crypto::cl::prover::Prover;
    ///
    /// let _proof_builder = Prover::new_proof_builder();
    pub fn new_proof_builder<'a>() -> Result<ProofBuilder<'a>, IndyCryptoError> {

        Ok(ProofBuilder {
            authz_proof_init: None,
            common_attributes: BTreeMap::new(),
            init_proofs: BTreeMap::new(),
            c_list: Vec::new(),
            tau_list: Vec::new(),
        })
    }

    fn _check_credential_key_correctness_proof(
        pr_pub_key: &CredentialPrimaryPublicKey,
        key_correctness_proof: &CredentialKeyCorrectnessProof,
    ) -> Result<(), IndyCryptoError> {
        trace!(
            "Prover::_check_credential_key_correctness_proof: >>> pr_pub_key: {:?}, key_correctness_proof: {:?}",
            pr_pub_key,
            key_correctness_proof
        );

        let mut ctx = BigNumber::new_context()?;

        let z_inverse = pr_pub_key.z.inverse(&pr_pub_key.n, Some(&mut ctx))?;
        let z_cap = get_pedersen_commitment(
            &z_inverse,
            &key_correctness_proof.c,
            &pr_pub_key.s,
            &key_correctness_proof.xz_cap,
            &pr_pub_key.n,
            &mut ctx,
        )?;

        let mut r_cap: BTreeMap<String, BigNumber> = BTreeMap::new();
        for (key, r_value) in pr_pub_key.r.iter() {
            let xr_cap_value = key_correctness_proof.xr_cap.get(key).ok_or(
                IndyCryptoError::InvalidStructure(format!(
                    "Value by key '{}' not found in key_correctness_proof.xr_cap",
                    key
                )),
            )?;

            let r_inverse = r_value.inverse(&pr_pub_key.n, Some(&mut ctx))?;
            let val = get_pedersen_commitment(
                &r_inverse,
                &key_correctness_proof.c,
                &pr_pub_key.s,
                &xr_cap_value,
                &pr_pub_key.n,
                &mut ctx,
            )?;

            r_cap.insert(key.to_owned(), val);
        }

        let mut values: Vec<u8> = Vec::new();
        values.extend_from_slice(&pr_pub_key.z.to_bytes()?);
        for val in pr_pub_key.r.values() {
            values.extend_from_slice(&val.to_bytes()?);
        }
        values.extend_from_slice(&z_cap.to_bytes()?);
        for val in r_cap.values() {
            values.extend_from_slice(&val.to_bytes()?);
        }

        let c = get_hash_as_int(&mut vec![values])?;

        let valid = key_correctness_proof.c.eq(&c);

        if !valid {
            return Err(IndyCryptoError::InvalidStructure(
                format!("Invalid Credential key correctness proof"),
            ));
        }

        trace!("Prover::_check_credential_key_correctness_proof: <<<");

        Ok(())
    }

    // TODO: Fixme; This should be private, making it public temporarily
    pub fn _generate_primary_blinded_credential_secrets(
        p_pub_key: &CredentialPrimaryPublicKey,
        credential_values: &CredentialValues,
    ) -> Result<PrimaryBlindedCredentialSecretsFactors, IndyCryptoError> {
        trace!(
            "Prover::_generate_blinded_primary_master_secret: >>> p_pub_key: {:?}, credential_values: {:?}",
            p_pub_key,
            credential_values
        );

        let mut ctx = BigNumber::new_context()?;
        let v_prime = bn_rand(LARGE_VPRIME)?;

        //Hidden attributes are combined in this value
        let hidden_attributes = credential_values
            .attrs_values
            .iter()
            .filter(|&(_, v)| v.is_hidden())
            .map(|(attr, _)| attr.clone())
            .collect::<BTreeSet<String>>();
        let u = hidden_attributes.iter().fold(
            p_pub_key.s.mod_exp(
                &v_prime,
                &p_pub_key.n,
                Some(&mut ctx),
            ),
            |acc, attr| {
                let pk_r = p_pub_key.r.get(&attr.clone()).ok_or(
                    IndyCryptoError::InvalidStructure(
                        format!("Value by key '{}' not found in pk.r", attr),
                    ),
                )?;
                let cred_value = &credential_values.attrs_values[attr];
                acc?.mod_mul(
                    &pk_r.mod_exp(
                        cred_value.value(),
                        &p_pub_key.n,
                        Some(&mut ctx),
                    )?,
                    &p_pub_key.n,
                    Some(&mut ctx),
                )
            },
        )?;


        let mut committed_attributes = BTreeMap::new();

        for (attr, cv) in credential_values.attrs_values.iter().filter(|&(_, v)| {
            v.is_commitment()
        })
        {
            if let &CredentialValue::Commitment {
                ref value,
                ref blinding_factor,
            } = cv
            {
                committed_attributes.insert(
                    attr.clone(),
                    get_pedersen_commitment(
                        &p_pub_key.s,
                        blinding_factor,
                        &p_pub_key.z,
                        value,
                        &p_pub_key.n,
                        &mut ctx,
                    )?,
                );
            }
        }

        let primary_blinded_cred_secrets = PrimaryBlindedCredentialSecretsFactors {
            u,
            v_prime,
            hidden_attributes,
            committed_attributes,
        };

        trace!(
            "Prover::_generate_blinded_primary_master_secret: <<< primary_blinded_cred_secrets: {:?}",
            primary_blinded_cred_secrets
        );

        Ok(primary_blinded_cred_secrets)
    }

    fn _generate_revocation_blinded_credential_secrets(
        r_pub_key: &CredentialRevocationPublicKey,
    ) -> Result<RevocationBlindedCredentialSecretsFactors, IndyCryptoError> {
        trace!(
            "Prover::_generate_revocation_blinded_credential_secrets: >>> r_pub_key: {:?}",
            r_pub_key
        );

        let vr_prime = GroupOrderElement::new()?;
        let ur = r_pub_key.h2.mul(&vr_prime)?;

        let revocation_blinded_cred_secrets =
            RevocationBlindedCredentialSecretsFactors { ur, vr_prime };

        trace!(
            "Prover::_generate_revocation_blinded_credential_secrets: <<< revocation_blinded_cred_secrets: {:?}",
            revocation_blinded_cred_secrets
        );

        Ok(revocation_blinded_cred_secrets)
    }

    fn _new_blinded_credential_secrets_correctness_proof(
        p_pub_key: &CredentialPrimaryPublicKey,
        primary_blinded_cred_secrets: &PrimaryBlindedCredentialSecretsFactors,
        nonce: &BigNumber,
        cred_values: &CredentialValues,
    ) -> Result<BlindedCredentialSecretsCorrectnessProof, IndyCryptoError> {
        trace!(
            "Prover::_new_blinded_credential_secrets_correctness_proof: >>> p_pub_key: {:?}, primary_blinded_cred_secrets: {:?}, nonce: {:?}, cred_values: {:?}",
            primary_blinded_cred_secrets,
            nonce,
            p_pub_key,
            cred_values
        );

        let mut ctx = BigNumber::new_context()?;

        let v_dash_tilde = bn_rand(LARGE_VPRIME_TILDE)?;

        let mut m_tildes = BTreeMap::new();
        let mut r_tildes = BTreeMap::new();

        let mut values: Vec<u8> = Vec::new();
        let mut u_tilde = p_pub_key.s.mod_exp(
            &v_dash_tilde,
            &p_pub_key.n,
            Some(&mut ctx),
        )?;

        for (attr, cred_value) in
            cred_values.attrs_values.iter().filter(|&(_, v)| {
                v.is_hidden() || v.is_commitment()
            })
        {
            let m_tilde = bn_rand(LARGE_MTILDE)?;
            let pk_r = p_pub_key.r.get(attr).ok_or(
                IndyCryptoError::InvalidStructure(
                    format!(
                        "Value by key '{}' not found in pk.r",
                        attr
                    ),
                ),
            )?;

            match *cred_value {
                CredentialValue::Hidden { .. } => {
                    u_tilde = u_tilde.mod_mul(
                        &pk_r.mod_exp(&m_tilde, &p_pub_key.n, Some(&mut ctx))?,
                        &p_pub_key.n,
                        Some(&mut ctx),
                    )?;
                    ()
                }
                CredentialValue::Commitment { .. } => {
                    let r_tilde = bn_rand(LARGE_MTILDE)?;
                    let commitment_tilde = get_pedersen_commitment(
                        &p_pub_key.z,
                        &m_tilde,
                        &p_pub_key.s,
                        &r_tilde,
                        &p_pub_key.n,
                        &mut ctx,
                    )?;
                    r_tildes.insert(attr.clone(), r_tilde);

                    values.extend_from_slice(&commitment_tilde.to_bytes()?);
                    let ca_value = primary_blinded_cred_secrets.committed_attributes
                                                               .get(attr)
                                                               .ok_or(IndyCryptoError::InvalidStructure(format!("Value by key '{}' not found in primary_blinded_cred_secrets.committed_attributes", attr)))?;
                    values.extend_from_slice(&ca_value.to_bytes()?);
                    ()
                }
                _ => (),
            }
            m_tildes.insert(attr.clone(), m_tilde);
        }

        values.extend_from_slice(&primary_blinded_cred_secrets.u.to_bytes()?);
        values.extend_from_slice(&u_tilde.to_bytes()?);
        values.extend_from_slice(&nonce.to_bytes()?);

        let c = get_hash_as_int(&vec![values])?;

        let v_dash_cap = c.mul(&primary_blinded_cred_secrets.v_prime, Some(&mut ctx))?
            .add(&v_dash_tilde)?;

        let mut m_caps = BTreeMap::new();
        let mut r_caps = BTreeMap::new();

        for (attr, m_tilde) in &m_tildes {
            let ca = cred_values.attrs_values.get(attr).ok_or(
                IndyCryptoError::InvalidStructure(format!(
                    "Value by key '{}' not found in cred_values.committed_attributes",
                    attr
                )),
            )?;

            match ca {
                &CredentialValue::Hidden { ref value } => {
                    let m_cap = m_tilde.add(&c.mul(value, Some(&mut ctx))?)?;
                    m_caps.insert(attr.clone(), m_cap);
                    ()
                }
                &CredentialValue::Commitment {
                    ref value,
                    ref blinding_factor,
                } => {
                    let m_cap = m_tilde.add(&c.mul(value, Some(&mut ctx))?)?;
                    let r_cap = r_tildes[attr].add(&c.mul(blinding_factor, Some(&mut ctx))?)?;

                    m_caps.insert(attr.clone(), m_cap);
                    r_caps.insert(attr.clone(), r_cap);
                    ()
                }
                _ => (),
            }
        }

        let blinded_credential_secrets_correctness_proof =
            BlindedCredentialSecretsCorrectnessProof {
                c,
                v_dash_cap,
                m_caps,
                r_caps,
            };

        trace!(
            "Prover::_new_blinded_credential_secrets_correctness_proof: <<< blinded_primary_master_secret_correctness_proof: {:?}",
            blinded_credential_secrets_correctness_proof
        );

        Ok(blinded_credential_secrets_correctness_proof)
    }

    fn _process_primary_credential(
        p_cred: &mut PrimaryCredentialSignature,
        v_prime: &BigNumber,
    ) -> Result<(), IndyCryptoError> {
        trace!(
            "Prover::_process_primary_credential: >>> p_cred: {:?}, v_prime: {:?}",
            p_cred,
            v_prime
        );

        p_cred.v = v_prime.add(&p_cred.v)?;

        trace!("Prover::_process_primary_credential: <<<");

        Ok(())
    }

    fn _process_non_revocation_credential(
        r_cred: &mut NonRevocationCredentialSignature,
        vr_prime: &GroupOrderElement,
        cred_rev_pub_key: &CredentialRevocationPublicKey,
        rev_key_pub: &RevocationKeyPublic,
        rev_reg: &RevocationRegistry,
        witness: &Witness,
    ) -> Result<(), IndyCryptoError> {
        trace!(
            "Prover::_process_non_revocation_credential: >>> r_cred: {:?}, vr_prime: {:?}, cred_rev_pub_key: {:?}, rev_reg: {:?}, rev_key_pub: {:?}",
            r_cred,
            vr_prime,
            cred_rev_pub_key,
            rev_reg,
            rev_key_pub
        );

        let r_cnxt_m2 = BigNumber::from_bytes(&r_cred.m2.to_bytes()?)?;
        r_cred.vr_prime_prime = vr_prime.add_mod(&r_cred.vr_prime_prime)?;
        Prover::_test_witness_signature(
            &r_cred,
            cred_rev_pub_key,
            rev_key_pub,
            rev_reg,
            witness,
            &r_cnxt_m2,
        )?;

        trace!("Prover::_process_non_revocation_credential: <<<");

        Ok(())
    }

    fn _check_signature_correctness_proof(
        p_cred_sig: &PrimaryCredentialSignature,
        cred_values: &CredentialValues,
        signature_correctness_proof: &SignatureCorrectnessProof,
        p_pub_key: &CredentialPrimaryPublicKey,
        nonce: &Nonce,
    ) -> Result<(), IndyCryptoError> {
        trace!(
            "Prover::_check_signature_correctness_proof: >>> p_cred_sig: {:?}, \
                                                                cred_values: {:?}, \
                                                                signature_correctness_proof: {:?}, \
                                                                p_pub_key: {:?}, \
                                                                nonce: {:?}",
            p_cred_sig,
            cred_values,
            signature_correctness_proof,
            p_pub_key,
            nonce
        );

        let mut ctx = BigNumber::new_context()?;

        if !p_cred_sig.e.is_prime(Some(&mut ctx))? {
            return Err(IndyCryptoError::InvalidStructure(
                format!("Invalid Signature correctness proof"),
            ));
        }

        let rx = cred_values
            .attrs_values
            .iter()
            .filter(|&(ref attr, ref value)| {
                (value.is_known() || value.is_hidden()) && p_pub_key.r.contains_key(attr.clone())
            })
            .fold(
                get_pedersen_commitment(
                    &p_pub_key.s,
                    &p_cred_sig.v,
                    &p_pub_key.rctxt,
                    &p_cred_sig.m_2,
                    &p_pub_key.n,
                    &mut ctx,
                ),
                |acc, (attr, value)| {
                    acc?.mod_mul(
                        &p_pub_key.r[&attr.clone()].mod_exp(
                            value.value(),
                            &p_pub_key.n,
                            Some(&mut ctx),
                        )?,
                        &p_pub_key.n,
                        Some(&mut ctx),
                    )
                },
            )?;

        //        let mut generators_and_exponents = cred_values.attrs_values
        //                                                      .iter()
        //                                                      .filter(|&(ref key, ref value)| (value.is_known() || value.is_hidden()) && p_pub_key.r.contains_key(key.clone()))
        //                                                      .map(|(key, value)| (p_pub_key.r.get(&key.clone()).unwrap(), value.value())).collect::<Vec<(&BigNumber, &BigNumber)>>();
        //        generators_and_exponents.push((&p_pub_key.s, &p_cred_sig.v));
        //        generators_and_exponents.push((&p_pub_key.rctxt, &p_cred_sig.m_2));
        //
        //        for (key, attr) in cred_values.attrs_values.iter() {
        //            let pk_r = p_pub_key.r
        //                .get(key)
        //                .ok_or(IndyCryptoError::InvalidStructure(format!("Value by key '{}' not found in pk.r", key)))?;
        //            let value = attr.value()?;
        //
        //            match attr {
        //                &CredentialValue::Known{..} => {
        //                    generators_and_exponents.push((&pk_r, value));
        //                    ()
        //                },
        //                &CredentialValue::Hidden{..} => {
        //                    generators_and_exponents.push((&pk_r, value));
        //                    ()
        //                },
        //                _ => ()
        //            };
        //        }
        //
        //        let rx = get_exponentiated_generators(generators_and_exponents, &p_pub_key.n, &mut ctx)?;

        let q = p_pub_key.z.mod_div(&rx, &p_pub_key.n, Some(&mut ctx))?;

        let expected_q = p_cred_sig.a.mod_exp(
            &p_cred_sig.e,
            &p_pub_key.n,
            Some(&mut ctx),
        )?;

        if !q.eq(&expected_q) {
            return Err(IndyCryptoError::InvalidStructure(
                format!("Invalid Signature correctness proof"),
            ));
        }

        let degree = signature_correctness_proof.c.add(
            &signature_correctness_proof.se.mul(
                &p_cred_sig.e,
                Some(&mut ctx),
            )?,
        )?;

        let a_cap = p_cred_sig.a.mod_exp(&degree, &p_pub_key.n, Some(&mut ctx))?;

        let mut values: Vec<u8> = Vec::new();
        values.extend_from_slice(&q.to_bytes()?);
        values.extend_from_slice(&p_cred_sig.a.to_bytes()?);
        values.extend_from_slice(&a_cap.to_bytes()?);
        values.extend_from_slice(&nonce.to_bytes()?);

        let c = get_hash_as_int(&vec![values])?;

        let valid = signature_correctness_proof.c.eq(&c);

        if !valid {
            return Err(IndyCryptoError::InvalidStructure(
                format!("Invalid Signature correctness proof"),
            ));
        }

        trace!("Prover::_check_signature_correctness_proof: <<<");

        Ok(())
    }

    fn _test_witness_signature(
        r_cred: &NonRevocationCredentialSignature,
        cred_rev_pub_key: &CredentialRevocationPublicKey,
        rev_key_pub: &RevocationKeyPublic,
        rev_reg: &RevocationRegistry,
        witness: &Witness,
        r_cnxt_m2: &BigNumber,
    ) -> Result<(), IndyCryptoError> {
        trace!(
            "Prover::_test_witness_signature: >>> r_cred: {:?}, cred_rev_pub_key: {:?}, rev_key_pub: {:?}, rev_reg: {:?}, r_cnxt_m2: {:?}",
            r_cred,
            cred_rev_pub_key,
            rev_key_pub,
            rev_reg,
            r_cnxt_m2
        );

        let z_calc = Pair::pair(&r_cred.witness_signature.g_i, &rev_reg.accum)?
            .mul(&Pair::pair(&cred_rev_pub_key.g, &witness.omega)?.inverse()?)?;

        if z_calc != rev_key_pub.z {
            return Err(IndyCryptoError::InvalidStructure(
                "Issuer is sending incorrect data".to_string(),
            ));
        }
        let pair_gg_calc = Pair::pair(
            &cred_rev_pub_key.pk.add(&r_cred.g_i)?,
            &r_cred.witness_signature.sigma_i,
        )?;
        let pair_gg = Pair::pair(&cred_rev_pub_key.g, &cred_rev_pub_key.g_dash)?;

        if pair_gg_calc != pair_gg {
            return Err(IndyCryptoError::InvalidStructure(
                "Issuer is sending incorrect data".to_string(),
            ));
        }

        let m2 = GroupOrderElement::from_bytes(&r_cnxt_m2.to_bytes()?)?;

        let pair_h1 = Pair::pair(
            &r_cred.sigma,
            &cred_rev_pub_key.y.add(
                &cred_rev_pub_key.h_cap.mul(&r_cred.c)?,
            )?,
        )?;
        let pair_h2 = Pair::pair(
            &cred_rev_pub_key
                .h0
                .add(&cred_rev_pub_key.h1.mul(&m2)?)?
                .add(&cred_rev_pub_key.h2.mul(&r_cred.vr_prime_prime)?)?
                .add(&r_cred.g_i)?,
            &cred_rev_pub_key.h_cap,
        )?;

        if pair_h1 != pair_h2 {
            return Err(IndyCryptoError::InvalidStructure(
                "Issuer is sending incorrect data".to_string(),
            ));
        }

        trace!("Prover::_test_witness_signature: <<<");

        Ok(())
    }
}

#[derive(Debug)]
pub struct ProofBuilder<'a> {
    authz_proof_init: Option<AuthzProofInit<'a>>,
    common_attributes: BTreeMap<String, BigNumber>,
    init_proofs: BTreeMap<String, InitProof>,
    c_list: Vec<Vec<u8>>,
    tau_list: Vec<Vec<u8>>,
}

impl<'a> ProofBuilder<'a> {
    /// Creates m_tildes for attributes that will be the same across all subproofs
    pub fn add_common_attribute(&mut self, attr_name: &str) -> Result<(), IndyCryptoError> {
        self.common_attributes.insert(attr_name.to_owned(), bn_rand(LARGE_MVECT)?);
        Ok(())
    }

    pub fn add_authz_proof_request(&mut self, authz_proof_factors: &'a AuthzProofFactors) -> Result<(), IndyCryptoError> {
        trace!("ProofBuilder::add_authz_proof_request: >>> authz_proof_factors: {:?}", authz_proof_factors);
        if !self.common_attributes.contains_key(&authz_proof_factors.policy_address_attr_name) {
            return Err(IndyCryptoError::InvalidStructure(format!("{0} is marked as a common attribute. Please call ProofBuilder.add_common_attribute({0})", authz_proof_factors.policy_address_attr_name)));
        }

        let policy_address_m_tilde = &self.common_attributes[&authz_proof_factors.policy_address_attr_name];

        self.authz_proof_init = Some(AuthzProofInit::init(authz_proof_factors, &policy_address_m_tilde)?);

        Ok(())
    }

    /// Adds sub proof request to proof builder which will be used fo building of proof.
    /// Part of proof request related to a particular schema-key.
    ///
    /// # Arguments
    /// * `proof_builder` - Proof builder.
    /// * `key_id` - Unique credential identifier.
    /// * `sub_proof_request` -Requested attributes and predicates.
    /// * `credential_schema` - Credential schema.
    /// * `credential_signature` - Credential signature.
    /// * `credential_values` - Credential values.
    /// * `credential_pub_key` - Credential public key.
    /// * `rev_reg_pub` - (Optional) Revocation registry public.
    ///
    /// #Example
    /// ```
    /// use indy_crypto::cl::new_nonce;
    /// use indy_crypto::cl::issuer::Issuer;
    /// use indy_crypto::cl::prover::Prover;
    /// use indy_crypto::cl::verifier::Verifier;
    ///
    /// let mut credential_schema_builder = Issuer::new_credential_schema_builder().unwrap();
    /// credential_schema_builder.add_attr("sex").unwrap();
    /// let credential_schema = credential_schema_builder.finalize().unwrap();
    ///
    /// let (credential_pub_key, credential_priv_key, cred_key_correctness_proof) = Issuer::new_credential_def(&credential_schema, false).unwrap();
    ///
    /// let master_secret = Prover::new_master_secret().unwrap();
    /// let master_secret_blinding_nonce = new_nonce().unwrap();
    /// let (blinded_master_secret, master_secret_blinding_data, blinded_master_secret_correctness_proof) =
    ///     Prover::blind_master_secret(&credential_pub_key, &cred_key_correctness_proof, &master_secret, &master_secret_blinding_nonce).unwrap();
    ///
    /// let mut credential_values_builder = Issuer::new_credential_values_builder().unwrap();
    /// credential_values_builder.add_value("sex", "5944657099558967239210949258394887428692050081607692519917050011144233115103").unwrap();
    /// let credential_values = credential_values_builder.finalize().unwrap();
    ///
    /// let credential_issuance_nonce = new_nonce().unwrap();
    ///
    /// let (mut credential_signature, signature_correctness_proof) =
    ///     Issuer::sign_credential("CnEDk9HrMnmiHXEV1WFgbVCRteYnPqsJwrTdcZaNhFVW",
    ///                             &blinded_master_secret,
    ///                             &blinded_master_secret_correctness_proof,
    ///                             &master_secret_blinding_nonce,
    ///                             &credential_issuance_nonce,
    ///                             &credential_values,
    ///                             &credential_pub_key,
    ///                             &credential_priv_key).unwrap();
    ///
    /// Prover::process_credential_signature(&mut credential_signature,
    ///                                      &credential_values,
    ///                                      &signature_correctness_proof,
    ///                                      &master_secret_blinding_data,
    ///                                      &master_secret,
    ///                                      &credential_pub_key,
    ///                                      &credential_issuance_nonce,
    ///                                      None, None, None).unwrap();
    ///
    /// let mut sub_proof_request_builder = Verifier::new_sub_proof_request_builder().unwrap();
    /// sub_proof_request_builder.add_revealed_attr("sex").unwrap();
    /// let sub_proof_request = sub_proof_request_builder.finalize().unwrap();
    ///
    /// let mut proof_builder = Prover::new_proof_builder().unwrap();
    /// proof_builder.add_sub_proof_request("issuer_key_id_1",
    ///                                     &sub_proof_request,
    ///                                     &credential_schema,
    ///                                     &credential_signature,
    ///                                     &credential_values,
    ///                                     &credential_pub_key,
    ///                                     None,
    ///                                     None).unwrap();
    /// ```
    pub fn add_sub_proof_request(&mut self,
                                 key_id: &str,
                                 sub_proof_request: &SubProofRequest,
                                 credential_schema: &CredentialSchema,
                                 non_credential_schema_elements: &NonCredentialSchemaElements,
                                 credential_signature: &CredentialSignature,
                                 credential_values: &CredentialValues,
                                 credential_pub_key: &CredentialPublicKey,
                                 rev_reg: Option<&RevocationRegistry>,
                                 witness: Option<&Witness>) -> Result<(), IndyCryptoError> {
        trace!("ProofBuilder::add_sub_proof_request: >>> key_id: {:?}\n\
                                                         credential_signature: {:?}\n\
                                                         credential_values: {:?}\n\
                                                         credential_pub_key: {:?}\n\
                                                         rev_reg: {:?}\n\
                                                         sub_proof_request: {:?}\n\
                                                         credential_schema: {:?}",
               key_id, credential_signature, credential_values, credential_pub_key, rev_reg, sub_proof_request, credential_schema);

        ProofBuilder::_check_add_sub_proof_request_params_consistency(
            credential_values,
            sub_proof_request,
            credential_schema,
            non_credential_schema_elements,
        )?;

        let mut non_revoc_init_proof = None;
        let mut m2_tilde: Option<BigNumber> = None;

        if let (&Some(ref r_cred), &Some(ref r_reg), &Some(ref r_pub_key), &Some(ref witness)) =
            (
                &credential_signature.r_credential,
                &rev_reg,
                &credential_pub_key.r_key,
                &witness,
            )
        {
            let (m, p) = self.add_sub_proof_request_revocation(
                &r_cred,
                &r_reg,
                &r_pub_key,
                &witness,
            )?;
            m2_tilde = Some(m);
            non_revoc_init_proof = Some(p);
        }

        let primary_init_proof = self.add_sub_proof_request_primary(
            &credential_pub_key.p_key,
            &credential_signature.p_credential,
            credential_values,
            credential_schema,
            non_credential_schema_elements,
            sub_proof_request,
            m2_tilde,
        )?;
        let init_proof = InitProof {
            primary_init_proof,
            non_revoc_init_proof,
            credential_values: credential_values.clone()?,
            sub_proof_request: sub_proof_request.clone(),
            credential_schema: credential_schema.clone(),
            non_credential_schema_elements: non_credential_schema_elements.clone(),
        };
        self.init_proofs.insert(key_id.to_owned(), init_proof);

        trace!("ProofBuilder::add_sub_proof_request: <<<");

        Ok(())
    }

    /// Finalize proof.
    ///
    /// # Arguments
    /// * `proof_builder` - Proof builder.
    /// * `nonce` - Nonce.
    /// * `master_secret` - Master secret.
    ///
    /// #Example
    /// ```
    /// use indy_crypto::cl::new_nonce;
    /// use indy_crypto::cl::issuer::Issuer;
    /// use indy_crypto::cl::prover::Prover;
    /// use indy_crypto::cl::verifier::Verifier;
    ///
    /// let mut credential_schema_builder = Issuer::new_credential_schema_builder().unwrap();
    /// credential_schema_builder.add_attr("sex").unwrap();
    /// let credential_schema = credential_schema_builder.finalize().unwrap();
    ///
    /// let (credential_pub_key, credential_priv_key, cred_key_correctness_proof) = Issuer::new_credential_def(&credential_schema, false).unwrap();
    ///
    /// let master_secret = Prover::new_master_secret().unwrap();
    /// let master_secret_blinding_nonce = new_nonce().unwrap();
    /// let (blinded_master_secret, master_secret_blinding_data, blinded_master_secret_correctness_proof) =
    ///     Prover::blind_master_secret(&credential_pub_key, &cred_key_correctness_proof, &master_secret, &master_secret_blinding_nonce).unwrap();
    ///
    /// let mut credential_values_builder = Issuer::new_credential_values_builder().unwrap();
    /// credential_values_builder.add_value("sex", "5944657099558967239210949258394887428692050081607692519917050011144233115103").unwrap();
    /// let credential_values = credential_values_builder.finalize().unwrap();
    ///
    /// let credential_issuance_nonce = new_nonce().unwrap();
    ///
    /// let (mut credential_signature, signature_correctness_proof) =
    ///     Issuer::sign_credential("CnEDk9HrMnmiHXEV1WFgbVCRteYnPqsJwrTdcZaNhFVW",
    ///                             &blinded_master_secret,
    ///                             &blinded_master_secret_correctness_proof,
    ///                             &master_secret_blinding_nonce,
    ///                             &credential_issuance_nonce,
    ///                             &credential_values,
    ///                             &credential_pub_key,
    ///                             &credential_priv_key).unwrap();
    ///
    /// Prover::process_credential_signature(&mut credential_signature,
    ///                                      &credential_values,
    ///                                      &signature_correctness_proof,
    ///                                      &master_secret_blinding_data,
    ///                                      &master_secret,
    ///                                      &credential_pub_key,
    ///                                      &credential_issuance_nonce,
    ///                                      None, None, None).unwrap();
    ///
    /// let mut sub_proof_request_builder = Verifier::new_sub_proof_request_builder().unwrap();
    /// sub_proof_request_builder.add_revealed_attr("sex").unwrap();
    /// let sub_proof_request = sub_proof_request_builder.finalize().unwrap();
    ///
    /// let mut proof_builder = Prover::new_proof_builder().unwrap();
    /// proof_builder.add_sub_proof_request("issuer_key_id_1",
    ///                                     &sub_proof_request,
    ///                                     &credential_schema,
    ///                                     &credential_signature,
    ///                                     &credential_values,
    ///                                     &credential_pub_key,
    ///                                     None,
    ///                                     None).unwrap();
    ///
    /// let proof_request_nonce = new_nonce().unwrap();
    /// let _proof = proof_builder.finalize(&proof_request_nonce, &master_secret).unwrap();
    /// ```
    pub fn finalize(&self, nonce: &Nonce) -> Result<Proof, IndyCryptoError> {
        trace!("ProofBuilder::finalize: >>> nonce: {:?}", nonce);

        let mut values: Vec<Vec<u8>> = Vec::new();
        values.extend_from_slice(&self.tau_list);
        values.extend_from_slice(&self.c_list);

        if let Some(ref authz_proof_init) = self.authz_proof_init {
            authz_proof_init.add_t_list(&mut values);
        }

        values.push(nonce.to_bytes()?);

        // In the anoncreds whitepaper, `challenge` is denoted by `c_h`
        let challenge = get_hash_as_int(&values)?;

        let mut proofs: BTreeMap<String, SubProof> = BTreeMap::new();

        let mut policy_address_m_hat = None;

        for (proof_cred_uuid, init_proof) in self.init_proofs.iter() {
            let mut non_revoc_proof: Option<NonRevocProof> = None;
            if let Some(ref non_revoc_init_proof) = init_proof.non_revoc_init_proof {
                non_revoc_proof = Some(ProofBuilder::_finalize_non_revocation_proof(
                    &non_revoc_init_proof,
                    &challenge,
                )?);
            }

            let primary_proof = ProofBuilder::_finalize_primary_proof(
                &init_proof.primary_init_proof,
                &challenge,
                &init_proof.credential_schema,
                &init_proof.non_credential_schema_elements,
                &init_proof.credential_values,
                &init_proof.sub_proof_request,
            )?;

            if policy_address_m_hat == None {
                if let Some(ref authz_proof_init) = self.authz_proof_init {
                    let m_hat = primary_proof.eq_proof.m[authz_proof_init.get_policy_address_attr_name()].clone()?;
                    policy_address_m_hat = Some(m_hat);
                }
            }

            let proof = SubProof { primary_proof, non_revoc_proof };
            proofs.insert(proof_cred_uuid.to_owned(), proof);
        }

        let authz_proof =
                if let Some(ref authz_proof_init) = self.authz_proof_init {
                    Some(authz_proof_init.finalize(&challenge, &policy_address_m_hat.unwrap())?)
                } else {
                    None
                };

        let aggregated_proof = AggregatedProof { c_hash: challenge, c_list: self.c_list.clone() };

        let proof = Proof { proofs, aggregated_proof, authz_proof };

        trace!("ProofBuilder::finalize: <<< proof: {:?}", proof);

        Ok(proof)
    }

    // TODO: Fixme; This method should be private
    pub fn add_sub_proof_request_primary(
        &mut self,
        primary_public_key: &CredentialPrimaryPublicKey,
        primary_credential: &PrimaryCredentialSignature,
        credential_values: &CredentialValues,
        credential_schema: &CredentialSchema,
        non_credential_schema_elements: &NonCredentialSchemaElements,
        sub_proof_request: &SubProofRequest,
        m2_tilde: Option<BigNumber>,
    ) -> Result<PrimaryInitProof, IndyCryptoError> {
        let primary_init_proof = ProofBuilder::_init_primary_proof(
            &self.common_attributes,
            &primary_public_key,
            &primary_credential,
            credential_values,
            credential_schema,
            non_credential_schema_elements,
            sub_proof_request,
            m2_tilde,
        )?;
        self.c_list.extend_from_slice(
            &primary_init_proof.as_c_list()?,
        );
        self.tau_list.extend_from_slice(
            &primary_init_proof.as_tau_list()?,
        );
        Ok(primary_init_proof)
    }

    // TODO: Fixme; This method should be private
    pub fn add_sub_proof_request_revocation(
        &mut self,
        revocation_credential: &NonRevocationCredentialSignature,
        rev_reg: &RevocationRegistry,
        cred_rev_pub_key: &CredentialRevocationPublicKey,
        witness: &Witness,
    ) -> Result<(BigNumber, NonRevocInitProof), IndyCryptoError> {
        let proof = ProofBuilder::_init_non_revocation_proof(
            &revocation_credential,
            &rev_reg,
            &cred_rev_pub_key,
            &witness,
        )?;

        self.c_list.extend_from_slice(&proof.as_c_list()?);
        self.tau_list.extend_from_slice(&proof.as_tau_list()?);
        let m2_tilde = group_element_to_bignum(&proof.tau_list_params.m2)?;
        let non_revoc_init_proof = proof;
        Ok((m2_tilde, non_revoc_init_proof))
    }

    pub fn _check_add_sub_proof_request_params_consistency(
        cred_values: &CredentialValues,
        sub_proof_request: &SubProofRequest,
        cred_schema: &CredentialSchema,
        non_credential_schema_elements: &NonCredentialSchemaElements,
    ) -> Result<(), IndyCryptoError> {
        trace!(
            "ProofBuilder::_check_add_sub_proof_request_params_consistency: >>> cred_values: {:?}, sub_proof_request: {:?}, cred_schema: {:?}",
            cred_values,
            sub_proof_request,
            cred_schema
        );

        let schema_attrs = non_credential_schema_elements
            .attrs
            .union(&cred_schema.attrs)
            .cloned()
            .collect::<BTreeSet<String>>();

        let cred_attrs = BTreeSet::from_iter(cred_values.attrs_values.keys().cloned());

        if schema_attrs != cred_attrs {
            return Err(IndyCryptoError::InvalidStructure(format!(
                "Credential doesn't correspond to credential schema"
            )));
        }

        if sub_proof_request
            .revealed_attrs
            .difference(&cred_attrs)
            .count() != 0
        {
            return Err(IndyCryptoError::InvalidStructure(
                format!("Credential doesn't contain requested attribute"),
            ));
        }

        let predicates_attrs = sub_proof_request
            .predicates
            .iter()
            .map(|predicate| predicate.attr_name.clone())
            .collect::<BTreeSet<String>>();

        if predicates_attrs.difference(&cred_attrs).count() != 0 {
            return Err(IndyCryptoError::InvalidStructure(format!(
                "Credential doesn't contain attribute requested in predicate"
            )));
        }

        trace!("ProofBuilder::_check_add_sub_proof_request_params_consistency: <<<");

        Ok(())
    }

    fn _init_primary_proof(
        common_attributes: &BTreeMap<String, BigNumber>,
        issuer_pub_key: &CredentialPrimaryPublicKey,
        c1: &PrimaryCredentialSignature,
        cred_values: &CredentialValues,
        cred_schema: &CredentialSchema,
        non_cred_schema_elems: &NonCredentialSchemaElements,
        sub_proof_request: &SubProofRequest,
        m2_t: Option<BigNumber>,
    ) -> Result<PrimaryInitProof, IndyCryptoError> {
        trace!(
            "ProofBuilder::_init_primary_proof: >>> issuer_pub_key: {:?}, c1: {:?}, cred_values: {:?}, cred_schema: {:?}, sub_proof_request: {:?}, m2_t: {:?}",
            issuer_pub_key,
            c1,
            cred_values,
            cred_schema,
            sub_proof_request,
            m2_t
        );

        let eq_proof = ProofBuilder::_init_eq_proof(
            common_attributes,
            &issuer_pub_key,
            c1,
            cred_schema,
            non_cred_schema_elems,
            sub_proof_request,
            m2_t,
        )?;

        let mut ge_proofs: Vec<PrimaryPredicateGEInitProof> = Vec::new();
        for predicate in sub_proof_request.predicates.iter() {
            let ge_proof = ProofBuilder::_init_ge_proof(
                &issuer_pub_key,
                &eq_proof.m_tilde,
                cred_values,
                predicate,
            )?;
            ge_proofs.push(ge_proof);
        }

        let primary_init_proof = PrimaryInitProof {
            eq_proof,
            ge_proofs,
        };

        trace!(
            "ProofBuilder::_init_primary_proof: <<< primary_init_proof: {:?}",
            primary_init_proof
        );

        Ok(primary_init_proof)
    }

    fn _init_non_revocation_proof(
        r_cred: &NonRevocationCredentialSignature,
        rev_reg: &RevocationRegistry,
        cred_rev_pub_key: &CredentialRevocationPublicKey,
        witness: &Witness,
    ) -> Result<NonRevocInitProof, IndyCryptoError> {
        trace!(
            "ProofBuilder::_init_non_revocation_proof: >>> r_cred: {:?}, rev_reg: {:?}, cred_rev_pub_key: {:?}, witness: {:?}",
            r_cred,
            rev_reg,
            cred_rev_pub_key,
            witness
        );

        let c_list_params = ProofBuilder::_gen_c_list_params(&r_cred)?;
        let c_list = ProofBuilder::_create_c_list_values(
            &r_cred,
            &c_list_params,
            &cred_rev_pub_key,
            witness,
        )?;

        let tau_list_params = ProofBuilder::_gen_tau_list_params()?;
        let tau_list =
            create_tau_list_values(&cred_rev_pub_key, &rev_reg, &tau_list_params, &c_list)?;

        let r_init_proof = NonRevocInitProof {
            c_list_params,
            tau_list_params,
            c_list,
            tau_list,
        };

        trace!(
            "ProofBuilder::_init_non_revocation_proof: <<< r_init_proof: {:?}",
            r_init_proof
        );

        Ok(r_init_proof)
    }

    fn _init_eq_proof(
        common_attributes: &BTreeMap<String, BigNumber>,
        cred_pub_key: &CredentialPrimaryPublicKey,
        c1: &PrimaryCredentialSignature,
        cred_schema: &CredentialSchema,
        non_cred_schema_elems: &NonCredentialSchemaElements,
        sub_proof_request: &SubProofRequest,
        m2_t: Option<BigNumber>,
    ) -> Result<PrimaryEqualInitProof, IndyCryptoError> {
        trace!(
            "ProofBuilder::_init_eq_proof: >>> cred_pub_key: {:?}, c1: {:?}, cred_schema: {:?}, sub_proof_request: {:?}, m2_t: {:?}",
            cred_pub_key,
            c1,
            cred_schema,
            sub_proof_request,
            m2_t
        );

        let mut ctx = BigNumber::new_context()?;

        let m2_tilde = m2_t.unwrap_or(bn_rand(LARGE_MVECT)?);

        let r = bn_rand(LARGE_VPRIME)?;
        let e_tilde = bn_rand(LARGE_ETILDE)?;
        let v_tilde = bn_rand(LARGE_VTILDE)?;

        let mut unrevealed_attrs = non_cred_schema_elems.attrs.union(&cred_schema.attrs)
                                                              .cloned()
                                                              .collect::<BTreeSet<String>>()
                                                              .difference(&sub_proof_request.revealed_attrs)
                                                              .cloned()
                                                              .collect::<BTreeSet<String>>();

        let mut m_tilde = clone_bignum_map(&common_attributes)?;
        get_mtilde(&unrevealed_attrs, &mut m_tilde)?;

        let a_prime = cred_pub_key.s
            .mod_exp(&r, &cred_pub_key.n, Some(&mut ctx))?
            .mod_mul(&c1.a, &cred_pub_key.n, Some(&mut ctx))?;

        let v_prime = c1.v.sub(&c1.e.mul(&r, Some(&mut ctx))?)?;

        let e_prime = c1.e.sub(&BigNumber::from_u32(2)?.exp(
            &BigNumber::from_dec(
                &LARGE_E_START.to_string(),
            )?,
            Some(&mut ctx),
        )?)?;

        let t = calc_teq(
            &cred_pub_key,
            &a_prime,
            &e_tilde,
            &v_tilde,
            &m_tilde,
            &m2_tilde,
            &unrevealed_attrs,
        )?;

        let t = calc_teq(&cred_pub_key, &a_prime, &e_tilde, &v_tilde, &m_tilde, &m2_tilde, &unrevealed_attrs)?;

        let primary_equal_init_proof = PrimaryEqualInitProof {
            a_prime,
            t,
            e_tilde,
            e_prime,
            v_tilde,
            v_prime,
            m_tilde,
            m2_tilde: m2_tilde.clone()?,
            m2: c1.m_2.clone()?,
        };

        trace!(
            "ProofBuilder::_init_eq_proof: <<< primary_equal_init_proof: {:?}",
            primary_equal_init_proof
        );

        Ok(primary_equal_init_proof)
    }

    fn _init_ge_proof(
        p_pub_key: &CredentialPrimaryPublicKey,
        m_tilde: &BTreeMap<String, BigNumber>,
        cred_values: &CredentialValues,
        predicate: &Predicate,
    ) -> Result<PrimaryPredicateGEInitProof, IndyCryptoError> {
        trace!(
            "ProofBuilder::_init_ge_proof: >>> p_pub_key: {:?}, m_tilde: {:?}, cred_values: {:?}, predicate: {:?}",
            p_pub_key,
            m_tilde,
            cred_values,
            predicate
        );

        let mut ctx = BigNumber::new_context()?;
        let (k, value) = (&predicate.attr_name, predicate.value);

        let attr_value = cred_values
            .attrs_values
            .get(k.as_str())
            .ok_or(IndyCryptoError::InvalidStructure(
                format!("Value by key '{}' not found in cred_values", k),
            ))?
            .value()
            .to_dec()?
            .parse::<i32>()
            .map_err(|_| {
                IndyCryptoError::InvalidStructure(
                    format!("Value by key '{}' has invalid format", k),
                )
            })?;

        let delta: i32 = attr_value - value;

        if delta < 0 {
            return Err(IndyCryptoError::InvalidStructure(
                "Predicate is not satisfied".to_string(),
            ));
        }

        let u = four_squares(delta)?;

        let mut r: BTreeMap<String, BigNumber> = BTreeMap::new();
        let mut t: BTreeMap<String, BigNumber> = BTreeMap::new();
        let mut c_list: Vec<BigNumber> = Vec::new();

        for i in 0..ITERATION {
            let cur_u = u.get(&i.to_string()).ok_or(
                IndyCryptoError::InvalidStructure(
                    format!(
                        "Value by key '{}' not found in u1",
                        i
                    ),
                ),
            )?;

            let cur_r = bn_rand(LARGE_VPRIME)?;
            let cut_t = get_pedersen_commitment(
                &p_pub_key.z,
                &cur_u,
                &p_pub_key.s,
                &cur_r,
                &p_pub_key.n,
                &mut ctx,
            )?;

            r.insert(i.to_string(), cur_r);
            t.insert(i.to_string(), cut_t.clone()?);
            c_list.push(cut_t)
        }

        let r_delta = bn_rand(LARGE_VPRIME)?;

        let t_delta = get_pedersen_commitment(
            &p_pub_key.z,
            &BigNumber::from_dec(&delta.to_string())?,
            &p_pub_key.s,
            &r_delta,
            &p_pub_key.n,
            &mut ctx,
        )?;

        r.insert("DELTA".to_string(), r_delta);
        t.insert("DELTA".to_string(), t_delta.clone()?);
        c_list.push(t_delta);

        let mut u_tilde: BTreeMap<String, BigNumber> = BTreeMap::new();
        let mut r_tilde: BTreeMap<String, BigNumber> = BTreeMap::new();

        for i in 0..ITERATION {
            u_tilde.insert(i.to_string(), bn_rand(LARGE_UTILDE)?);
            r_tilde.insert(i.to_string(), bn_rand(LARGE_RTILDE)?);
        }

        r_tilde.insert("DELTA".to_string(), bn_rand(LARGE_RTILDE)?);
        let alpha_tilde = bn_rand(LARGE_ALPHATILDE)?;

        let mj = m_tilde.get(k.as_str()).ok_or(
            IndyCryptoError::InvalidStructure(
                format!(
                    "Value by key '{}' not found in eq_proof.mtilde",
                    k
                ),
            ),
        )?;

        let tau_list = calc_tge(&p_pub_key, &u_tilde, &r_tilde, &mj, &alpha_tilde, &t)?;

        let primary_predicate_ge_init_proof = PrimaryPredicateGEInitProof {
            c_list,
            tau_list,
            u,
            u_tilde,
            r,
            r_tilde,
            alpha_tilde,
            predicate: predicate.clone(),
            t,
        };

        trace!(
            "ProofBuilder::_init_ge_proof: <<< primary_predicate_ge_init_proof: {:?}",
            primary_predicate_ge_init_proof
        );

        Ok(primary_predicate_ge_init_proof)
    }

    fn _finalize_eq_proof(
        init_proof: &PrimaryEqualInitProof,
        challenge: &BigNumber,
        cred_schema: &CredentialSchema,
        non_cred_schema_elems: &NonCredentialSchemaElements,
        cred_values: &CredentialValues,
        sub_proof_request: &SubProofRequest,
    ) -> Result<PrimaryEqualProof, IndyCryptoError> {
        trace!(
            "ProofBuilder::_finalize_eq_proof: >>> init_proof: {:?}, challenge: {:?}, cred_schema: {:?}, \
        cred_values: {:?}, sub_proof_request: {:?}",
            init_proof,
            challenge,
            cred_schema,
            cred_values,
            sub_proof_request
        );

        let mut ctx = BigNumber::new_context()?;

        let e = challenge.mul(&init_proof.e_prime, Some(&mut ctx))?.add(
            &init_proof.e_tilde,
        )?;

        let v = challenge.mul(&init_proof.v_prime, Some(&mut ctx))?.add(
            &init_proof.v_tilde,
        )?;

        let mut m: BTreeMap<String, BigNumber> = BTreeMap::new();

        let unrevealed_attrs = non_cred_schema_elems
            .attrs
            .union(&cred_schema.attrs)
            .cloned()
            .collect::<BTreeSet<String>>()
            .difference(&sub_proof_request.revealed_attrs)
            .cloned()
            .collect::<BTreeSet<String>>();

        for k in unrevealed_attrs.iter() {
            let cur_mtilde = init_proof.m_tilde.get(k).ok_or(
                IndyCryptoError::InvalidStructure(
                    format!(
                        "Value by key '{}' not found in init_proof.mtilde",
                        k
                    ),
                ),
            )?;

            let cur_val = cred_values.attrs_values.get(k).ok_or(
                IndyCryptoError::InvalidStructure(format!(
                    "Value by key '{}' not found in attributes_values",
                    k
                )),
            )?;

            let val = challenge.mul(&cur_val.value(), Some(&mut ctx))?.add(
                &cur_mtilde,
            )?;

            m.insert(k.clone(), val);
        }

        let m2 = challenge.mul(&init_proof.m2, Some(&mut ctx))?.add(
            &init_proof.m2_tilde,
        )?;

        let mut revealed_attrs_with_values: BTreeMap<String, BigNumber> = BTreeMap::new();

        for attr in sub_proof_request.revealed_attrs.iter() {
            revealed_attrs_with_values.insert(
                attr.clone(),
                cred_values
                    .attrs_values
                    .get(attr)
                    .ok_or(IndyCryptoError::InvalidStructure(
                        format!("Encoded value not found"),
                    ))?
                    .value()
                    .clone()?,
            );
        }

        let primary_equal_proof = PrimaryEqualProof {
            revealed_attrs: revealed_attrs_with_values,
            a_prime: init_proof.a_prime.clone()?,
            e,
            v,
            m,
            m2,
        };

        trace!(
            "ProofBuilder::_finalize_eq_proof: <<< primary_equal_proof: {:?}",
            primary_equal_proof
        );

        Ok(primary_equal_proof)
    }

    fn _finalize_ge_proof(
        c_h: &BigNumber,
        init_proof: &PrimaryPredicateGEInitProof,
        eq_proof: &PrimaryEqualProof,
    ) -> Result<PrimaryPredicateGEProof, IndyCryptoError> {
        trace!(
            "ProofBuilder::_finalize_ge_proof: >>> c_h: {:?}, init_proof: {:?}, eq_proof: {:?}",
            c_h,
            init_proof,
            eq_proof
        );

        let mut ctx = BigNumber::new_context()?;
        let mut u: BTreeMap<String, BigNumber> = BTreeMap::new();
        let mut r: BTreeMap<String, BigNumber> = BTreeMap::new();
        let mut urproduct = BigNumber::new()?;

        for i in 0..ITERATION {
            let cur_utilde = &init_proof.u_tilde[&i.to_string()];
            let cur_u = &init_proof.u[&i.to_string()];
            let cur_rtilde = &init_proof.r_tilde[&i.to_string()];
            let cur_r = &init_proof.r[&i.to_string()];

            let new_u: BigNumber = c_h.mul(&cur_u, Some(&mut ctx))?.add(&cur_utilde)?;
            let new_r: BigNumber = c_h.mul(&cur_r, Some(&mut ctx))?.add(&cur_rtilde)?;

            u.insert(i.to_string(), new_u);
            r.insert(i.to_string(), new_r);

            urproduct = cur_u.mul(&cur_r, Some(&mut ctx))?.add(&urproduct)?;

            let cur_rtilde_delta = &init_proof.r_tilde["DELTA"];

            let new_delta = c_h.mul(&init_proof.r["DELTA"], Some(&mut ctx))?.add(
                &cur_rtilde_delta,
            )?;

            r.insert("DELTA".to_string(), new_delta);
        }

        let alpha = init_proof.r["DELTA"]
            .sub(&urproduct)?
            .mul(&c_h, Some(&mut ctx))?
            .add(&init_proof.alpha_tilde)?;

        let primary_predicate_ge_proof = PrimaryPredicateGEProof {
            u,
            r,
            mj: eq_proof.m[&init_proof.predicate.attr_name].clone()?,
            alpha,
            t: clone_bignum_map(&init_proof.t)?,
            predicate: init_proof.predicate.clone(),
        };

        trace!(
            "ProofBuilder::_finalize_ge_proof: <<< primary_predicate_ge_proof: {:?}",
            primary_predicate_ge_proof
        );

        Ok(primary_predicate_ge_proof)
    }

    // TODO: Fixme; Needs to be private
    pub fn _finalize_primary_proof(
        init_proof: &PrimaryInitProof,
        challenge: &BigNumber,
        cred_schema: &CredentialSchema,
        non_cred_schema_elems: &NonCredentialSchemaElements,
        cred_values: &CredentialValues,
        sub_proof_request: &SubProofRequest,
    ) -> Result<PrimaryProof, IndyCryptoError> {
        trace!(
            "ProofBuilder::_finalize_primary_proof: >>> init_proof: {:?}, challenge: {:?}, cred_schema: {:?}, \
        cred_values: {:?}, sub_proof_request: {:?}",
            init_proof,
            challenge,
            cred_schema,
            cred_values,
            sub_proof_request
        );

        let eq_proof = ProofBuilder::_finalize_eq_proof(
            &init_proof.eq_proof,
            challenge,
            cred_schema,
            non_cred_schema_elems,
            cred_values,
            sub_proof_request,
        )?;
        let mut ge_proofs: Vec<PrimaryPredicateGEProof> = Vec::new();

        for init_ge_proof in init_proof.ge_proofs.iter() {
            let ge_proof = ProofBuilder::_finalize_ge_proof(challenge, init_ge_proof, &eq_proof)?;
            ge_proofs.push(ge_proof);
        }

        let primary_proof = PrimaryProof {
            eq_proof,
            ge_proofs,
        };

        trace!(
            "ProofBuilder::_finalize_primary_proof: <<< primary_proof: {:?}",
            primary_proof
        );

        Ok(primary_proof)
    }

    fn _gen_c_list_params(
        r_cred: &NonRevocationCredentialSignature,
    ) -> Result<NonRevocProofXList, IndyCryptoError> {
        trace!("ProofBuilder::_gen_c_list_params: >>> r_cred: {:?}", r_cred);

        let rho = GroupOrderElement::new()?;
        let r = GroupOrderElement::new()?;
        let r_prime = GroupOrderElement::new()?;
        let r_prime_prime = GroupOrderElement::new()?;
        let r_prime_prime_prime = GroupOrderElement::new()?;
        let o = GroupOrderElement::new()?;
        let o_prime = GroupOrderElement::new()?;
        let m = rho.mul_mod(&r_cred.c)?;
        let m_prime = r.mul_mod(&r_prime_prime)?;
        let t = o.mul_mod(&r_cred.c)?;
        let t_prime = o_prime.mul_mod(&r_prime_prime)?;
        let m2 = GroupOrderElement::from_bytes(&r_cred.m2.to_bytes()?)?;

        let non_revoc_proof_x_list = NonRevocProofXList {
            rho,
            r,
            r_prime,
            r_prime_prime,
            r_prime_prime_prime,
            o,
            o_prime,
            m,
            m_prime,
            t,
            t_prime,
            m2,
            s: r_cred.vr_prime_prime,
            c: r_cred.c,
        };

        trace!(
            "ProofBuilder::_gen_c_list_params: <<< non_revoc_proof_x_list: {:?}",
            non_revoc_proof_x_list
        );

        Ok(non_revoc_proof_x_list)
    }

    fn _create_c_list_values(
        r_cred: &NonRevocationCredentialSignature,
        params: &NonRevocProofXList,
        r_pub_key: &CredentialRevocationPublicKey,
        witness: &Witness,
    ) -> Result<NonRevocProofCList, IndyCryptoError> {
        trace!(
            "ProofBuilder::_create_c_list_values: >>> r_cred: {:?}, r_pub_key: {:?}",
            r_cred,
            r_pub_key
        );

        let e = r_pub_key.h.mul(&params.rho)?.add(&r_pub_key.htilde.mul(
            &params.o,
        )?)?;

        let d = r_pub_key.g.mul(&params.r)?.add(&r_pub_key.htilde.mul(
            &params.o_prime,
        )?)?;

        let a = r_cred.sigma.add(&r_pub_key.htilde.mul(&params.rho)?)?;

        let g = r_cred.g_i.add(&r_pub_key.htilde.mul(&params.r)?)?;

        let w = witness.omega.add(&r_pub_key.h_cap.mul(&params.r_prime)?)?;

        let s = r_cred.witness_signature.sigma_i.add(&r_pub_key.h_cap.mul(
            &params.r_prime_prime,
        )?)?;

        let u = r_cred.witness_signature.u_i.add(&r_pub_key.h_cap.mul(
            &params.r_prime_prime_prime,
        )?)?;

        let non_revoc_proof_c_list = NonRevocProofCList {
            e,
            d,
            a,
            g,
            w,
            s,
            u,
        };

        trace!(
            "ProofBuilder::_create_c_list_values: <<< non_revoc_proof_c_list: {:?}",
            non_revoc_proof_c_list
        );

        Ok(non_revoc_proof_c_list)
    }

    fn _gen_tau_list_params() -> Result<NonRevocProofXList, IndyCryptoError> {
        trace!("ProofBuilder::_gen_tau_list_params: >>>");

        let non_revoc_proof_x_list = NonRevocProofXList {
            rho: GroupOrderElement::new()?,
            r: GroupOrderElement::new()?,
            r_prime: GroupOrderElement::new()?,
            r_prime_prime: GroupOrderElement::new()?,
            r_prime_prime_prime: GroupOrderElement::new()?,
            o: GroupOrderElement::new()?,
            o_prime: GroupOrderElement::new()?,
            m: GroupOrderElement::new()?,
            m_prime: GroupOrderElement::new()?,
            t: GroupOrderElement::new()?,
            t_prime: GroupOrderElement::new()?,
            m2: GroupOrderElement::new()?,
            s: GroupOrderElement::new()?,
            c: GroupOrderElement::new()?,
        };

        trace!(
            "ProofBuilder::_gen_tau_list_params: <<< Nnon_revoc_proof_x_list: {:?}",
            non_revoc_proof_x_list
        );

        Ok(non_revoc_proof_x_list)
    }

    fn _finalize_non_revocation_proof(
        init_proof: &NonRevocInitProof,
        c_h: &BigNumber,
    ) -> Result<NonRevocProof, IndyCryptoError> {
        trace!(
            "ProofBuilder::_finalize_non_revocation_proof: >>> init_proof: {:?}, c_h: {:?}",
            init_proof,
            c_h
        );

        let ch_num_z = bignum_to_group_element(&c_h)?;
        let mut x_list: Vec<GroupOrderElement> = Vec::new();

        for (x, y) in init_proof.tau_list_params.as_list()?.iter().zip(
            init_proof
                .c_list_params
                .as_list()?
                .iter(),
        )
        {
            x_list.push(x.add_mod(&ch_num_z.mul_mod(&y)?.mod_neg()?)?);
        }

        let non_revoc_proof = NonRevocProof {
            x_list: NonRevocProofXList::from_list(x_list),
            c_list: init_proof.c_list.clone(),
        };

        trace!(
            "ProofBuilder::_finalize_non_revocation_proof: <<< non_revoc_proof: {:?}",
            non_revoc_proof
        );

        Ok(non_revoc_proof)
    }
}

